(ns aoc2022.day17
  (:require [clojure.string :as str]
    [clojure.set :as set]
    [clojure.core.match :refer [match]]
    [clojure.pprint :refer [pprint]]
    [clojure.math :refer [signum]]))


(def all-shapes {
  [[0 0] [1 0] [2 0] [3 0]] "-"
  [[1 0] [0 1] [1 1] [2 1] [1 2]] "+"
  [[0 0] [1 0] [2 0] [2 1] [2 2]] "⅃"
  [[0 0] [0 1] [0 2] [0 3]] "|"
  [[0 0] [1 0] [0 1] [1 1]] "⏹"
})


(def jet-shifts {\> [1 0] \< [-1 0]})


(defn offset [shape [dx dy]]
  (mapv (fn [[x y]] [(+ x dx) (+ y dy)]) shape))


(defn shape-ok? [shape blocked] 
  (->> shape
    (some (fn [[x y :as pos]] 
      (or 
        (> 0 x)
        (> 0 y)
        (< 6 x)
        (contains? blocked pos))))
    (not)))


(defn fall-shape [shape level blocked jets]
  (loop [shape (offset shape [2 (+ level 4)]) 
         blocked blocked
         jets jets]
    (let [[[_ shift] & jets'] jets
          jet-shift (jet-shifts shift)
          jet-shape (offset shape jet-shift)
          shape-to-move-down (if (shape-ok? jet-shape blocked) jet-shape shape)
          shape-down (offset shape-to-move-down [0 -1])]
      (if (shape-ok? shape-down blocked) (recur shape-down blocked jets')
        (let [blocked' (apply conj blocked shape-to-move-down)
              level' (->> shape-to-move-down
                        (map #(get % 1))
                        (apply max level))]
          [level' blocked' jets'])))))


(defn find-cycle [shapes jets]
  (loop [level -1
         blocked (set nil)
         jets jets
         shapes shapes
         fallen-shapes 0
         shapes-history {} ;[(shape + jet index) => shape indices list]
         levels-history {}] ; shapes-count => levels  
   (let [jet-index (first (first jets))
         shape-symbol (all-shapes (first shapes))
         history-key [jet-index shape-symbol]
         levels-history' (assoc levels-history fallen-shapes level)
         prev-fallen-shapes (shapes-history history-key)]
    (if (= 2 (count prev-fallen-shapes))
      [(conj prev-fallen-shapes fallen-shapes) levels-history']
      (let [[level' blocked' jets'] (fall-shape (first shapes) level blocked jets)]
        (recur 
          level' 
          blocked' 
          jets' 
          (next shapes) 
          (inc fallen-shapes) 
          (update 
            shapes-history 
            history-key 
            (fn [prev] (if (list? prev) (conj prev fallen-shapes) (list fallen-shapes)))) 
          levels-history'))
      ))))


(defn calc-height [input target]
  (let 
    [jets 
      (->> (str/split input #"")
        (map #(get % 0))
        (map-indexed vector)
        (repeat)
        (mapcat identity))
    shapes
      (->> all-shapes
        (keys)
        (repeat)
        (mapcat identity))
    [[cycle-end cycle-start] history] (find-cycle shapes jets)
    cycle-length (- cycle-end cycle-start)
    cycles-count (quot (- target cycle-start) cycle-length)
    level-per-cycle (- (history cycle-end) (history cycle-start))
    shapes-after-cycles (- target (+ cycle-start (* cycle-length cycles-count)))]
    (+ 1 (history cycle-start) (* level-per-cycle cycles-count) (- (history (+ cycle-start shapes-after-cycles)) (history cycle-start)))))



